var mongodb = require('./db');
var Attention = require('./attention');

function User(user) {
	this.name = user.name;
	this.email = user.email;
	this.password = user.password;
}
module.exports = User;

User.prototype.save = function save(callback) {
	var user = {
		name:this.name,
		email:this.email,
		password:this.password
	};

	mongodb.open(function(err,db) {
		if(err) {
			return callback(err);
		}

		//读取users集合
		db.collection('users',function(err,collection) {
			if(err) {
				mongodb.close();
				return callback(err);
			}

			//为email属性添加索引
			collection.ensureIndex('email',{unique:true});
			//写入user文档
			collection.insert(user,{safe:true},function(err,user) {
				mongodb.close();
				callback(err,user);
			});
		});
	});
};

User.get = function get(userEmail,callback) {
	mongodb.open(function(err,db) {
		if(err) {
			return callback(err);
		}

		//读取users集合
		db.collection('users',function(err,collection) {
			if(err) {
				mongodb.close();
				return callback(err);
			}

			//查找email属性为userEmail的文档
			collection.findOne({email:userEmail},function(err,doc) {
				mongodb.close();
				if(doc) {
					var user = new User(doc);
					callback(err,user);
				} else {
					callback(err,null);
				}
			});
		});
	});
};
User.getAll = function getAll(user,callback) {
	mongodb.open(function(err,db) {
		if(err) {
			return callback(err);
		}
		db.collection('users',function(err,collection) {
			if(err) {
				mongodb.close();
				return callback(err);
			}
			collection.find({"email":{"$ne":user.email}}).toArray(function(err,docs) {
				mongodb.close();
				if(err) {
					return callback(err,null);
				}
				console.log(user.email);
				Attention.getAttention(user.email,function(err,attentionEmails) {
					// console.log(attentionEmails)

					var attentionBox = {};
					attentionEmails.forEach(function(value,index) {
						attentionBox[value.to]=1;
					});
					//console.log(attentionBox);
					docs.forEach(function(value,index) {
						if(value.email in attentionBox) {
							value.attention = 1;
						}
					});
					callback(null,docs);
				});
			});
		});
	});
};
